const collectionHolder=document.querySelector("[data-collection-holder]");const addButton=document.getElementById("add-text-btn");let index=collectionHolder.querySelectorAll(".collection-item").length;addButton.addEventListener("click",(()=>{const prototype=collectionHolder.dataset.prototype;const newForm=prototype.replace(/__name__/g,index);const wrapper=document.createElement("div");wrapper.className="collection-item";wrapper.innerHTML=newForm;collectionHolder.appendChild(wrapper);index++}));document.addEventListener("click",(function(e){if(e.target&&e.target.classList.contains("remove-text")){e.preventDefault();const collectionItem=e.target.closest(".collection-item");if(collectionItem){collectionItem.remove()}}}));